import styled from "styled-components";

export const InputContainer = styled.div`
  border: 1px solid #000000;
  border-radius: 20px;

  height: 62px;
  width: 80px;

  margin-top: 2%;

  input {
    background: transparent;
    border: 0;
    width: 85%;
    padding: 20px;
    margin: 5px;
  }
`;
