import styled from "styled-components";

export const ItemContainer = styled.div`
  width: 80%;
  border: solid 1px #000000;
  border-left: none;
  border-right: none;

  h3 {
    font-family: "Inter";
    font-style: normal;
    font-weight: 700;
    font-size: 20px;
    line-height: 24px;
    color: #000000;
  }
  p {
    font-family: "Inter";
    font-style: normal;
    font-weight: 700;
    font-size: 13px;
    line-height: 16px;
    color: rgba(0, 0, 0, 0.49);
  }
  a {
    font-family: "Inter";
    font-style: normal;
    font-weight: 700;
    font-size: 15px;
    line-height: 18px;
    tex-decoration: none;

    color: rgba(0, 0, 0, 0.8);
  }
`;

export const ItemText = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
`;
