import styled from "styled-components";

export const ButtonContainer = styled.button`
  background-color: #000000;
  border: none;
  border-radius: 50%;

  height: 58px;
  width: 12%;
  cursor: pointer;
  margin: 15px;

  &: hover {
    background-color: #00000080;
  }
`;
